import React, { useState } from "react";

const CalculatorButton = ({ label, onClick }) => (
  <button
    className="py-2 px-4 bg-gray-300 hover:bg-gray-400 rounded text-gray-800 font-semibold mx-1 my-1"
    onClick={() => onClick(label)}
  >
    {label}
  </button>
);

const App = () => {
  const [num1, setNum1] = useState("");
  const [num2, setNum2] = useState("");
  const [operator, setOperator] = useState("");
  const [result, setResult] = useState("");

  const handleNumberClick = (number) => {
    if (operator === "") {
      setNum1(num1 + number);
    } else {
      setNum2(num2 + number);
    }
  };

  const handleOperatorClick = (op) => {
    if (num1 !== "") {
      setOperator(op);
    }
  };

  const calculateResult = () => {
    if (num1 !== "" && num2 !== "") {
      switch (operator) {
        case "+":
          setResult(parseFloat(num1) + parseFloat(num2));
          break;
        case "-":
          setResult(parseFloat(num1) - parseFloat(num2));
          break;
        case "*":
          setResult(parseFloat(num1) * parseFloat(num2));
          break;
        case "/":
          if (parseFloat(num2) !== 0) {
            setResult(parseFloat(num1) / parseFloat(num2));
          } else {
            setResult("Cannot divide by zero");
          }
          break;
        default:
          setResult("Invalid operation");
      }
    }
  };

  const handleClear = () => {
    setNum1("");
    setNum2("");
    setOperator("");
    setResult("");
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg">
        <h1 className="text-2xl font-semibold mb-4">
          {num1} {operator} {num2}
        </h1>
        <div className="flex flex-wrap">
          <CalculatorButton label="7" onClick={handleNumberClick} />
          <CalculatorButton label="8" onClick={handleNumberClick} />
          <CalculatorButton label="9" onClick={handleNumberClick} />
          <CalculatorButton label="/" onClick={handleOperatorClick} />
          <CalculatorButton label="4" onClick={handleNumberClick} />
          <CalculatorButton label="5" onClick={handleNumberClick} />
          <CalculatorButton label="6" onClick={handleNumberClick} />
          <CalculatorButton label="*" onClick={handleOperatorClick} />
          <CalculatorButton label="1" onClick={handleNumberClick} />
          <CalculatorButton label="2" onClick={handleNumberClick} />
          <CalculatorButton label="3" onClick={handleNumberClick} />
          <CalculatorButton label="-" onClick={handleOperatorClick} />
          <CalculatorButton label="0" onClick={handleNumberClick} />
          <CalculatorButton label="." onClick={handleNumberClick} />
          <CalculatorButton label="=" onClick={calculateResult} />
          <CalculatorButton label="+" onClick={handleOperatorClick} />
          <CalculatorButton label="C" onClick={handleClear} />
        </div>
        {result !== "" && (
          <div className="mt-4">
            <strong>Result: </strong>
            {result}
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
