import { useState } from "react";
import { NavLink } from "react-router-dom";

const Header = () => {
  const [show, setShow] = useState(false);

  const toggle = () => {
    setShow(!show);
  };

  const closeMenu = () => {
    setShow(false);
  };

  return (
    <header className="bg-black text-white flex justify-between px-5 py-2 items-baseline">
      <div>
        <NavLink to="/" onClick={closeMenu}>
          <h1 className="hover:text-black-300 bg-white transition">Sample Logo</h1>
        </NavLink>

        {show && (
          <nav className="flex flex-col space-y-1 mt-3 items-center">
            <NavLink to="/about-us" onClick={closeMenu} className="hover:text-black-300 bg-white transition">
              About
            </NavLink>
            <NavLink to="/contact-us" onClick={closeMenu} className="hover:text-black-300 bg-white transition">
              Contact
            </NavLink>
          </nav>
        )}
      </div>

      {show ? (
        <button onClick={toggle} className="hidden sm:flex">
          <i className="fa-solid fa-xmark fa-lg hover:text-gray-300 transition"></i>
        </button>
      ) : (
        <button onClick={toggle} className="hidden sm:flex">
          <i className="fa-solid fa-bars fa-lg hover:text-gray-300 transition"></i>
        </button>
      )}

      <nav className="space-x-5 flex sm:hidden">
        <NavLink to="/about-us" onClick={closeMenu} className="hover:text-black-300 bg-white transition">
          About
        </NavLink>
        <NavLink to="/contact-us" onClick={closeMenu} className=" bg-white hover:text-black-300  transition">
          Contact
        </NavLink>
      </nav>
    </header>
  );
};

export default Header;
