import { useState } from "react";
import { NavLink } from "react-router-dom";

const Header = () => {
  const [show, setShow] = useState(false);

  const toggle = () => {
    setShow(!show);
  };

  const closeMenu = () => {
    setShow(false);
  };

  return (
    <header className="bg-black text-white flex justify-between px-5 py-2 items-baseline">
      <div>
        <NavLink to="/" onClick={closeMenu}>
          <h1>Sample Logo</h1>
        </NavLink>

        {(show || window.innerWidth >= 640) && (
          <nav className="flex flex-col space-y-1 mt-3 items-center">
            <NavLink to="/about-us" onClick={closeMenu}>
              About
            </NavLink>
            <NavLink to="/contact-us" onClick={closeMenu}>
              Contact
            </NavLink>
          </nav>
        )}
      </div>

      <button onClick={toggle} className={`hidden sm:flex ${show ? "active" : ""}`}>
        <i className={`fa-solid ${show ? "fa-xmark" : "fa-bars"} fa-lg`}></i>
      </button>

      <nav className={`space-x-5 flex sm:hidden ${show ? "active" : ""}`}>
        <NavLink to="/about-us" onClick={closeMenu}>
          About
        </NavLink>
        <NavLink to="/contact-us" onClick={closeMenu}>
          Contact
        </NavLink>
      </nav>
    </header>
  );
};

export default Header;
