import React from 'react';
import featuredProduct from '../dummy/mealdata';
import quoteData from '../dummy/quotes';
import Quote from './Quote';

const importAll = (r) => {
  let images = {};
  r.keys().forEach((item) => {
    images[item.replace('./', '')] = r(item);
  });
  return images;
};

const imageFiles = importAll(require.context('../images/', false, /\.(jpg|jpeg|png)$/));

const HomePage = () => {
  const showDialogue = (quote) => {
    alert(quote);
  };

  return (
    <div>
      {/* Render the Quote component */}
      <Quote data={quoteData} show={showDialogue} />
      {featuredProduct.products.map((product) => (
        <div key={product.productId}>
          <h1>{product.strMeal}</h1>
          <img src={imageFiles[product.strMealThumb]} alt={product.strMeal} />
        </div>
      ))}
    </div>
  );
};

export default HomePage;
